mapper.select:
	SELECT CASE WHEN EXISTS(
	   					 	   SELECT *
	   						   FROM   USER
	   						   WHERE  1 = 1
	   						   AND    USER_ID = #{user_id}
	   						   AND    PWD     = #{pwd}
						   ) 
               THEN TRUE
		       ELSE FALSE 
		  END

/* com.quiz.mapper.userMapper.keepLogin */
UPDATE  USER
SET     SESSION_KEY   = #{session_id}
      , SESSION_LIMIT = #{session_limit}
WHERE   USER_ID       = #{user_id}
