mapper.select:
    	/* com.quiz.mapper.writingVoteDtlMapper.chekVote */
		SELECT  CASE WHEN COUNT(*) = 0 THEN 'N'
             		 ELSE 'Y'
        	    END AS CHEK_VOTE_VALID
		FROM    WRITING_VOTE WV
		WHERE   1 = 1
		AND     WV.WRITING_NO = #{writing_no}
		AND     WV.USER_ID    = #{user_id}
